/**
 * Protect a MA_OptLayout from the ATerm garbage collector. Every MA_OptLayout that is not rooted somewhere on the C call stack must be protected. Examples are global variables
 * \param[in] arg pointer to a MA_OptLayout
 */
void _MA_protectOptLayout(MA_OptLayout *arg) {
  ATprotect((ATerm*)((void*) arg));
}

/**
 * Unprotect a MA_OptLayout from the ATerm garbage collector. This improves the efficiency of the garbage collector, as well as provide opportunity for reclaiming space
 * \param[in] arg pointer to a MA_OptLayout
 */
void _MA_unprotectOptLayout(MA_OptLayout *arg) {
  ATunprotect((ATerm*)((void*) arg));
}

