/**
 * Assert whether a MA_FunId is any of the valid alternatives, or not. This analysis does not go any deeper than the top level
 * \param[in] arg input MA_FunId
 * \return ATtrue if #arg corresponds to the expected signature, or ATfalse otherwise
 */
ATbool MA_isValidFunId(MA_FunId arg) {
  if (MA_isFunIdCharacter(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdUnquoted(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdQuoted(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdReturnsList(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdList(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdIgnored(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdTranslate(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdTraversal(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdConcat(arg)) {
    return ATtrue;
  }
  else if (MA_isFunIdConcatDash(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

