/**
 * Assert whether a MA_TermList has a elems. 
 * \param[in] arg input MA_TermList
 * \return ATtrue if the MA_TermList had a elems, or ATfalse otherwise
 */
ATbool MA_hasTermListElems(MA_TermList arg) {
  if (MA_isTermListDefault(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

/**
 * Get the elems MA_TermElems of a MA_TermList. Note that the precondition is that this MA_TermList actually has a elems
 * \param[in] arg input MA_TermList
 * \return the elems of #arg, if it exist or an undefined value if it does not
 */
MA_TermElems MA_getTermListElems(MA_TermList arg) {
  
    return (MA_TermElems)ATgetArgument((ATermAppl)ATgetFirst((ATermList)ATgetArgument((ATermAppl)arg, 1)), 1);
}

/**
 * Set the elems of a MA_TermList. The precondition being that this MA_TermList actually has a elems
 * \param[in] arg input MA_TermList
 * \param[in] elems new MA_TermElems to set in #arg
 * \return A new MA_TermList with elems at the right place, or a core dump if #arg did not have a elems
 */
MA_TermList MA_setTermListElems(MA_TermList arg, MA_TermElems elems) {
  if (MA_isTermListDefault(arg)) {
    return (MA_TermList)ATsetArgument((ATermAppl)arg, (ATerm)ATreplace((ATermList)ATgetArgument((ATermAppl)arg, 1), (ATerm)ATsetArgument((ATermAppl)ATgetFirst((ATermList)ATgetArgument((ATermAppl)arg, 1)), (ATerm)((ATerm) elems), 1), 0), 1);
  }

  ATabort("TermList has no Elems: %t\n", arg);
  return (MA_TermList)NULL;
}

/**
 * Assert whether a MA_SigArg is any of the valid alternatives, or not. This analysis does not go any deeper than the top level
 * \param[in] arg input MA_SigArg
 * \return ATtrue if #arg corresponds to the expected signature, or ATfalse otherwise
 */
ATbool MA_isValidSigArg(MA_SigArg arg) {
  if (MA_isSigArgNormal(arg)) {
    return ATtrue;
  }
  else if (MA_isSigArgStar(arg)) {
    return ATtrue;
  }
  else if (MA_isSigArgPlus(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

