/**
 * Assert whether a MA_SigArg has a ws-after-_. 
 * \param[in] arg input MA_SigArg
 * \return ATtrue if the MA_SigArg had a ws-after-_, or ATfalse otherwise
 */
ATbool MA_hasSigArgWsAfterUnderscore(MA_SigArg arg) {
  if (MA_isSigArgStar(arg)) {
    return ATtrue;
  }
  else if (MA_isSigArgPlus(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

/**
 * Get the ws-after-_ MA_OptLayout of a MA_SigArg. Note that the precondition is that this MA_SigArg actually has a ws-after-_
 * \param[in] arg input MA_SigArg
 * \return the ws-after-_ of #arg, if it exist or an undefined value if it does not
 */
MA_OptLayout MA_getSigArgWsAfterUnderscore(MA_SigArg arg) {
  if (MA_isSigArgStar(arg)) {
    return (MA_OptLayout)ATelementAt((ATermList)ATgetArgument((ATermAppl)arg, 1), 1);
  }
  else 
    return (MA_OptLayout)ATelementAt((ATermList)ATgetArgument((ATermAppl)arg, 1), 1);
}

/**
 * Set the ws-after-_ of a MA_SigArg. The precondition being that this MA_SigArg actually has a ws-after-_
 * \param[in] arg input MA_SigArg
 * \param[in] wsAfterUnderscore new MA_OptLayout to set in #arg
 * \return A new MA_SigArg with wsAfterUnderscore at the right place, or a core dump if #arg did not have a wsAfterUnderscore
 */
MA_SigArg MA_setSigArgWsAfterUnderscore(MA_SigArg arg, MA_OptLayout wsAfterUnderscore) {
  if (MA_isSigArgStar(arg)) {
    return (MA_SigArg)ATsetArgument((ATermAppl)arg, (ATerm)ATreplace((ATermList)ATgetArgument((ATermAppl)arg, 1), (ATerm)((ATerm) wsAfterUnderscore), 1), 1);
  }
  else if (MA_isSigArgPlus(arg)) {
    return (MA_SigArg)ATsetArgument((ATermAppl)arg, (ATerm)ATreplace((ATermList)ATgetArgument((ATermAppl)arg, 1), (ATerm)((ATerm) wsAfterUnderscore), 1), 1);
  }

  ATabort("SigArg has no WsAfterUnderscore: %t\n", arg);
  return (MA_SigArg)NULL;
}

/**
 * Assert whether a MA_FuncDef is any of the valid alternatives, or not. This analysis does not go any deeper than the top level
 * \param[in] arg input MA_FuncDef
 * \return ATtrue if #arg corresponds to the expected signature, or ATfalse otherwise
 */
ATbool MA_isValidFuncDef(MA_FuncDef arg) {
  if (MA_isFuncDefConstantNoAnnos(arg)) {
    return ATtrue;
  }
  else if (MA_isFuncDefConstantWithAnnos(arg)) {
    return ATtrue;
  }
  else if (MA_isFuncDefFuncNoAnnos(arg)) {
    return ATtrue;
  }
  else if (MA_isFuncDefFuncWithAnnos(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

