/**
 * Assert whether a MA_LexLayout has a ch. 
 * \param[in] arg input MA_LexLayout
 * \return ATtrue if the MA_LexLayout had a ch, or ATfalse otherwise
 */
ATbool MA_hasLexLayoutCh(MA_LexLayout arg) {
  if (MA_isLexLayoutWhitespace(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

/**
 * Get the ch char of a MA_LexLayout. Note that the precondition is that this MA_LexLayout actually has a ch
 * \param[in] arg input MA_LexLayout
 * \return the ch of #arg, if it exist or an undefined value if it does not
 */
char MA_getLexLayoutCh(MA_LexLayout arg) {
  
    return (char)MA_charToByte((ATerm)ATgetFirst((ATermList)ATgetArgument((ATermAppl)arg, 1)));
}

/**
 * Set the ch of a MA_LexLayout. The precondition being that this MA_LexLayout actually has a ch
 * \param[in] arg input MA_LexLayout
 * \param[in] ch new char to set in #arg
 * \return A new MA_LexLayout with ch at the right place, or a core dump if #arg did not have a ch
 */
MA_LexLayout MA_setLexLayoutCh(MA_LexLayout arg, char ch) {
  if (MA_isLexLayoutWhitespace(arg)) {
    return (MA_LexLayout)ATsetArgument((ATermAppl)arg, (ATerm)ATreplace((ATermList)ATgetArgument((ATermAppl)arg, 1), (ATerm)((ATerm) ((ATerm) MA_byteToChar(ch))), 0), 1);
  }

  ATabort("LexLayout has no Ch: %t\n", arg);
  return (MA_LexLayout)NULL;
}

/**
 * Assert whether a MA_LexStrChar is any of the valid alternatives, or not. This analysis does not go any deeper than the top level
 * \param[in] arg input MA_LexStrChar
 * \return ATtrue if #arg corresponds to the expected signature, or ATfalse otherwise
 */
ATbool MA_isValidLexStrChar(MA_LexStrChar arg) {
  if (MA_isLexStrCharNewline(arg)) {
    return ATtrue;
  }
  else if (MA_isLexStrCharTab(arg)) {
    return ATtrue;
  }
  else if (MA_isLexStrCharQuote(arg)) {
    return ATtrue;
  }
  else if (MA_isLexStrCharBackslash(arg)) {
    return ATtrue;
  }
  else if (MA_isLexStrCharDecimal(arg)) {
    return ATtrue;
  }
  else if (MA_isLexStrCharNormal(arg)) {
    return ATtrue;
  }
  return ATfalse;
}

